import React, { useState, useEffect } from "react";
import { Card, CardHeader, CardBody, CardFooter } from "@chakra-ui/react";
function App() {
  const [questions, setQuestions] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  useEffect(() => {
    generateQuestions();
  }, []);

  const generateQuestions = () => {
    fetch("http://localhost:8000/api/generate/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => setQuestions(data))
      .catch((error) => {
        console.error("Error:", error);
        setQuestions([]);
      });
  };

  const handleAnswerSelect = (questionId, selectedChoice) => {
    const answer = { question_id: questionId, selected_choice: selectedChoice };
    setUserAnswers((prevAnswers) => [...prevAnswers, answer]);

    const question = questions.find((q) => q.id === questionId);
    const isCorrect = selectedChoice === question.correct_choice;

    if (isCorrect) {
      console.log("Correct answer!");

      alert("Corect Answer");
    } else {
      alert("Wrong Answer");
    }
  };

  const submitAnswers = () => {
    fetch("http://localhost:8000/api/submit/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ user_answers: userAnswers }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setScore(data.score);
        setShowResult(true);
      })
      .catch((error) => {
        console.error("Error:", error);
        setScore(0);
        setShowResult(true);
      });
  };

  return (
    <div className="App">
      <h1>Random Question Generator</h1>
      {showResult ? (
        <div>
          <h2>Result</h2>
          <p>
            Score: {score} / {questions.length}
          </p>
        </div>
      ) : (
        <div>
          <h2>Questions</h2>
          {questions.map((question) => (
            <div key={question.id}>
              <h3>{question.question_text}</h3>
              <ul>
                <li>
                  <span>1</span>{" "}
                  <button
                    onClick={() =>
                      handleAnswerSelect(question.id, question.choice1)
                    }
                  >
                    {question.choice1}
                  </button>
                </li>
                <li>
                  <span>2</span>{" "}
                  <button
                    onClick={() =>
                      handleAnswerSelect(question.id, question.choice2)
                    }
                  >
                    {question.choice2}
                  </button>
                </li>
                <li>
                  <span>3</span>{" "}
                  <button
                    onClick={() =>
                      handleAnswerSelect(question.id, question.choice3)
                    }
                  >
                    {question.choice3}
                  </button>
                </li>
                <li>
                  <span>4</span>{" "}
                  <button
                    onClick={() =>
                      handleAnswerSelect(question.id, question.choice4)
                    }
                  >
                    {question.choice4}
                  </button>
                </li>
              </ul>
            </div>
          ))}
          <button onClick={submitAnswers}>Submit</button>
        </div>
      )}
    </div>
  );
}

export default App;
